    \d{2} - # hónap
    [^\n]   # minden, kivéve az új sort
    #..
}xms;

$Text =~ m{
    Teszt
    [ ]    # itt szóköznek kell lennie a „Teszt” és a „Regex” között
    Regex
}xms;

$Text =~ m{
    \A      # a szöveg eleje
    Tartalom # valamilyen szöveg
    \z      # a szöveg vége
}xms;

$MultilineText =~ m{
    \A                      # a szöveg eleje
    .*
    (?: \n Tartalom $ )+    # egy vagy több sor, amely ugyanazt a szöveget tartalmazza
    .*
    \z                      # a szöveg vége
}xms;

