## no critic (Modules::RequireExplicitPackage)
use strict;
use warnings;
use utf8;

use vars (qw($Self));

use Kernel::GenericInterface::Debugger;
use Kernel::GenericInterface::Operation::Test::Test;

use Kernel::System::VariableCheck qw(:all);

# SSL-tanúsítvány ellenőrzésének kihagyása (a RestoreDatabase használata tilos ebben a tesztben).
$Kernel::OM->ObjectParamAdd(
    'Kernel::System::UnitTest::Helper' => {
        SkipSSLVerify => 1,
    },
);
my $Helper = $Kernel::OM->Get('Kernel::System::UnitTest::Helper');

# véletlen szám kérése
my $RandomID = $Helper->GetRandomNumber();

# új felhasználó létrehozása a jelenlegi teszthez
my $UserLogin = $Helper->TestUserCreate(
    Groups => ['users'],
);
my $Password = $UserLogin;

my $UserID = $Kernel::OM->Get('Kernel::System::User')->UserLookup(
    UserLogin => $UserLogin,
);

# webszolgáltatás nevének beállítása
my $WebserviceName = '-Test-' . $RandomID;

# webszolgáltatás objektum létrehozása
my $WebserviceObject = $Kernel::OM->Get('Kernel::System::GenericInterface::Webservice');
$Self->Is(
    'Kernel::System::GenericInterface::Webservice',
    ref $WebserviceObject,
    "Webszolgáltatás objektum létrehozása",
);

my $WebserviceID = $WebserviceObject->WebserviceAdd(
    Name   => $WebserviceName,
    Config => {
        Debugger => {
            DebugThreshold => 'debug',
        },
        Provider => {
            Transport => {
                Type => '',
            },
        },
    },
    ValidID => 1,
    UserID  => 1,
);
$Self->True(
    $WebserviceID,
    "Webszolgáltatás hozzáadása",
);

# távoli gép lekérése néhány óvintézkedéssel bizonyos egységteszt rendszereknél
my $Host = $Helper->GetTestHTTPHostname();

my $ConfigObject = $Kernel::OM->Get('Kernel::Config');

# webszolgáltatás beállításának előkészítése
my $RemoteSystem =
    $ConfigObject->Get('HttpType')
    . '://'
    . $Host
    . '/'
    . $ConfigObject->Get('ScriptAlias')
    . '/nph-genericinterface.pl/WebserviceID/'
    . $WebserviceID;

my $WebserviceConfig = {
    Description =>
        'Teszt a SOAP-átviteli háttérprogramot használó jegycsatlakozóhoz.',
    Debugger => {
        DebugThreshold => 'debug',
        TestMode       => 1,
    },
    Provider => {
        Transport => {
            Type   => 'HTTP::SOAP',
            Config => {
                MaxLength => 10000000,
                NameSpace => 'http://otrs.org/SoapTestInterface/',
                Endpoint  => $RemoteSystem,
            },
        },
        Operation => {
            Test => {
                Type => 'Test::Test',
            },
        },
    },
    Requester => {
        Transport => {
            Type   => 'HTTP::SOAP',
            Config => {
                NameSpace => 'http://otrs.org/SoapTestInterface/',
                Encoding  => 'UTF-8',
                Endpoint  => $RemoteSystem,
            },
        },
        Invoker => {
            Test => {
                Type => 'Test::TestSimple'
                ,    # a meghívónak Test::TestSimple típusúnak kell lennie azért,
                     # hogy egy kérést szimuláljon egy távoli rendszerhez
            },
        },
    },
};

# webszolgáltatás frissítése valódi beállítással
# a frissítés szükséges, mert a webszolgáltatás-azonosítót
# használjuk a végponthoz a beállításban
my $WebserviceUpdate = $WebserviceObject->WebserviceUpdate(
    ID      => $WebserviceID,
    Name    => $WebserviceName,
    Config  => $WebserviceConfig,
    ValidID => 1,
    UserID  => $UserID,
);
$Self->True(
    $WebserviceUpdate,
    "A webszolgáltatás frissítve: $WebserviceID - $WebserviceName",
);

# hibakereső objektum
my $DebuggerObject = Kernel::GenericInterface::Debugger->new(
    DebuggerConfig => {
        DebugThreshold => 'debug',
        TestMode       => 1,
    },
    WebserviceID      => $WebserviceID,
    CommunicationType => 'Provider',
);
$Self->Is(
    ref $DebuggerObject,
    'Kernel::GenericInterface::Debugger',
    'A hibakereső-objektum helyesen lett példányosítva',
);

# tesztesetek meghatározása
my @Tests = (
    {
        Name           => 'Teszteset neve',
        SuccessRequest => 1,                  # 1 vagy 0
        RequestData    => {

            # ...és a tesztadatok
        },
        ExpectedReturnLocalData => {
            Data => {

                # ...elvárt helyi eredmények hozzáadása
            },
            Success => 1,                     # 1 vagy 0
        },
        ExpectedReturnRemoteData => {
            Data => {

                # ...elvárt távoli eredmények hozzáadása
            },
            Success => 1,                     # 1 vagy 0
        },
        Operation => 'Test',
    },

    # ...további tesztesetek hozzáadása
);

TEST:
for my $Test (@Tests) {

    # helyi objektum létrehozása
    my $LocalObject = "Kernel::GenericInterface::Operation::Test::$Test->{Operation}"->new(
        DebuggerObject => $DebuggerObject,
        WebserviceID   => $WebserviceID,
    );

    $Self->Is(
        "Kernel::GenericInterface::Operation::Test::$Test->{Operation}",
        ref $LocalObject,
        "$Test->{Name} - Helyi objektum létrehozása",
    );

    my %Auth = (
        UserLogin => $UserLogin,
        Password  => $Password,
    );
    if ( IsHashRefWithData( $Test->{Auth} ) ) {
        %Auth = %{ $Test->{Auth} };
    }

    # kérő indítása a webszolgáltatásunkkal
    my $LocalResult = $LocalObject->Run(
        WebserviceID => $WebserviceID,
        Invoker      => $Test->{Operation},
        Data         => {
            %Auth,
            %{ $Test->{RequestData} },
        },
    );

    # eredmény ellenőrzése
    $Self->Is(
        'HASH',
        ref $LocalResult,
        "$Test->{Name} - A helyi eredményszerkezet helyes",
    );

    # kérő objektum létrehozása
    my $RequesterObject = $Kernel::OM->Get('Kernel::GenericInterface::Requester');
    $Self->Is(
        'Kernel::GenericInterface::Requester',
        ref $RequesterObject,
        "$Test->{Name} - Kérő objektum létrehozása",
    );

    # kérő indítása a webszolgáltatásunkkal
    my $RequesterResult = $RequesterObject->Run(
        WebserviceID => $WebserviceID,
        Invoker      => $Test->{Operation},
        Data         => {
            %Auth,
            %{ $Test->{RequestData} },
        },
    );

    # eredmény ellenőrzése
    $Self->Is(
        'HASH',
        ref $RequesterResult,
        "$Test->{Name} - A kérő eredményszerkezete helyes",
    );

    $Self->Is(
        $RequesterResult->{Success},
        $Test->{SuccessRequest},
        "$Test->{Name} - A kérő sikeres eredményt adott",
    );

    # ...tesztek hozzáadása az eredményekhez
}

# webszolgáltatás törlése
my $WebserviceDelete = $WebserviceObject->WebserviceDelete(
    ID     => $WebserviceID,
    UserID => $UserID,
);
$Self->True(
    $WebserviceDelete,
    "Webszolgáltatás törölve: $WebserviceID",
);

# törölni kell a teszt alatt minden egyéb hozzáadott adatot, mivel a RestoreDatabase használata tilos.

1;
